import React, { Component } from 'react'

import {increment,decrement} from '../../actions/cart'
export default class CartList extends Component {
    constructor(){
        super()
        this.state ={
            cartlist:[]
        }
    }
   componentDidMount(){
        this.getState()
   }
   getState =() =>{
    this.setState({
        cartlist: this.props.getState().cart
    })
   }
    render() {
        console.log(this.state)
        return (
            <div>
                <table>
                    <thead>
                        <tr>
                            <th>id</th>
                            <th>商品名称</th>
                            <th>价格</th>
                            <th>数量</th>
                            <th>操作</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.cartlist.map(item=>{
                                return (
                                    <tr key={item.id}>
                                        <td>{item.id}</td>
                                        <td>{item.title}</td>
                                        <td>{item.price}</td>
                                        <td>
                                            <button 
                                                  onClick={ ()=>{
                                                      this.props.dispatch(decrement(item.id))
                                                  }
                                            }
                                            >-</button>
                                                <span>{item.amount}</span>
                                            <button
                                              onClick={ ()=>{
                                                this.props.dispatch(increment(item.id))
                                            }
                                        }>+</button>
                                        </td>
                                    </tr>
                                )
                            })
                        }
                    </tbody>
                </table>                   

            </div>
        )
    }
}
