import React, { Component } from 'react'
import {connect} from 'react-redux' 
import {increment,decrement} from '../../actions/cart'
class CartList extends Component {
    // constructor(){
    //     super()
    //     this.state ={
    //         cartlist:[]
    //     }
    // }



    render() {
        console.log(this.props)
        return (
            <div>
                <table>
                    <thead>
                        <tr>
                            <th>id</th>
                            <th>商品名称</th>
                            <th>价格</th>
                            <th>数量</th>
                            <th>操作</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.props.cartlist.map(item=>{
                                return (
                                    <tr key={item.id}>
                                        <td>{item.id}</td>
                                        <td>{item.title}</td>
                                        <td>{item.price}</td>
                                        <td>
                                            {/* <button 
                                                  onClick={ 
                                                  this.props.decrement.bind(this,item.id)
                                                 }
                                            >-</button> */}
                                               <button 
                                                  onClick={ 
                                                  this.props.decrement.bind(this,item.id)
                                                 }
                                            >-</button>
                            <span>{item.amount}</span>
                                            {/* <button
                                              onClick={ 
                                                this.props.increment.bind(this,item.id)
                                        }>+</button> */}
                                           <button 
                                                  onClick={ 
                                                  this.props.decrement.bind(this,item.id)
                                                 }
                                            >-</button>
                                        </td>
                                    </tr>
                                )
                            })
                        }
                    </tbody>
                </table>                   

            </div>
        )
    }
}
const mapStateToProps = (state) =>{
    //console.log(state)这里的state是getState返回的值
    return {
        cartlist: state.cart
    }
}
const mapState = dispatch =>{
    return {
        add: (id) => dispatch(increment(id)),
        reduce: (id) => dispatch(decrement(id))
    }
}
// export default connect(mapStateToProps,{decrement,increment})(CartList)
export default connect(mapStateToProps,mapState)(CartList)
