//数据初始化在reducer
import actionType from '../actions/actionType'
const initState = [{
    id:1,
    title: 'Apple',
    amount: 10,
    price: 888.66
},{
    id:2,
    title: 'Orange',
    price: 4444.66,
    amount: 12
}]


export default (state = initState, action) => {
    console.log(action)
    switch(action.type){
        case actionType.CART_AMOUNT_DECREAMENT:
        return state.map(item =>{
            console.log('加')
            if(item.id ===actionType.payload){
                item.amount += 1
            }
            return item;
        })
        case actionType.CART_AMOUNT_INCREAMENT:
            return state.map(item =>{
                console.log('减')
                if(item.id ===actionType.payload){
                    item.amount -= 1
                }
                return item;
            })
        default:
            return state
    }
}
